/**
 * Unit Tests for Password class
**/

#include <gtest/gtest.h>
#include "Password.h"

class PasswordTest : public ::testing::Test
{
	protected:
		PasswordTest(){} //constructor runs before each test
		virtual ~PasswordTest(){} //destructor cleans up after tests
		virtual void SetUp(){} //sets up before each test (after constructor)
		virtual void TearDown(){} //clean up after each test, (before destructor)
};

TEST(PasswordTest, alternating_case)
{
	Password my_password;
	ASSERT_TRUE( my_password.has_mixed_case("PaSsWoRd") );
	ASSERT_TRUE( my_password.has_mixed_case("Pass"));
	ASSERT_FALSE( my_password.has_mixed_case("pass"));
	ASSERT_FALSE( my_password.has_mixed_case("PASS"));
	ASSERT_FALSE( my_password.has_mixed_case(""));
	ASSERT_TRUE( my_password.has_mixed_case("paS"));
	ASSERT_TRUE( my_password.has_mixed_case("PAs"));
	ASSERT_FALSE( my_password.has_mixed_case("s"));
	ASSERT_FALSE( my_password.has_mixed_case("S"));
	ASSERT_FALSE( my_password.has_mixed_case("."));
	ASSERT_FALSE( my_password.has_mixed_case("A:"));
	ASSERT_FALSE( my_password.has_mixed_case("a:"));
	ASSERT_TRUE( my_password.has_mixed_case("Aaaaaaa"));
	ASSERT_TRUE( my_password.has_mixed_case("aAAAAAA"));
	ASSERT_TRUE( my_password.has_mixed_case("zZZZZZZ"));
	ASSERT_TRUE( my_password.has_mixed_case("Zzzzzzz"));

	ASSERT_TRUE( my_password.has_mixed_case(""));



}